  const rows = data;

  let continueWithIteration = true;

  while (continueWithIteration) {
    // 2D Array - [[rowIndex, spotIndex]]
    const rollsToRemove = [] as number[][];

    rows.forEach((row, rowIndex) => {
      const spots = row.split("");

      spots.forEach((spot, spotIndex) => {
        if (spot !== ".") {
          let adjacentRolls = 0;

          if (spots[spotIndex - 1] === "@") {
            adjacentRolls++;
          }
          if (spots[spotIndex + 1] === "@") {
            adjacentRolls++;
          }

          const previousRow = rows[rowIndex - 1];
          const nextRow = rows[rowIndex + 1];

          if (previousRow) {
            if (previousRow[spotIndex] === "@") {
              adjacentRolls++;
            }
            if (previousRow[spotIndex - 1] === "@") {
              adjacentRolls++;
            }
            if (previousRow[spotIndex + 1] === "@") {
              adjacentRolls++;
            }
          }

          if (nextRow) {
            if (nextRow[spotIndex] === "@") {
              adjacentRolls++;
            }
            if (nextRow[spotIndex - 1] === "@") {
              adjacentRolls++;
            }
            if (nextRow[spotIndex + 1] === "@") {
              adjacentRolls++;
            }
          }

          if (adjacentRolls < 4) {
            rollsToRemove.push([rowIndex, spotIndex]);
            removableRollsCount++;
          }
        }
      });
    });

    if (rollsToRemove.length >= 1) {
      rollsToRemove.forEach((roll) => {
        const rowIndex = roll[0];
        const spotIndex = roll[1];

        let string = rows[rowIndex];
        const arr = string.split("");
        arr[spotIndex] = ".";
        string = arr.join("");
        rows[rowIndex] = string;
      });
    } else {
      continueWithIteration = false;
    }
  }

  console.log(removableRollsCount);
